import {
  SET_ALERT,
  CLEAR_ALERTS
} from '../types';

export default (state, action) => {
  switch(action.type) {
    case SET_ALERT:
      return action.payload;
    //case CLEAR_ALERTS:
      //return [];
    default: 
      return state;
  };
};
