import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import moment from 'moment';

const GoalItem = goal => {
  const { _id, name, startDate, duration } = goal;
  let time = moment().startOf('day').diff(startDate, 'days') + 1;
  if (time < 0)
    time = 0;
  else if (time > duration)
    return null;

  return (
    <li className='collection-item'>
      <div className='flex'>
        <h3 className='vertical-center'>
          <Link to={{ pathname: `/goal/${_id}`, goal }}>
            {name}
          </Link>
        </h3>
      </div>
      <div>
        <span className='right'>Day: {time} / {duration}</span>
        <br/>
        <span className='right'>100%</span>
      </div>
    </li>
  );
};

GoalItem.propTypes = {
  goal: PropTypes.object.isRequired
}

export default GoalItem;
