import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import GoalContext from '../../contexts/goals/goalContext';
import GoalItem from './GoalItem';

const Goals = () => {
  const goalContext = useContext(GoalContext);
  const { getUserGoals, userGoals, loading } = goalContext;

  const [activeGoals, setActiveGoals] = useState([]);
  const [pastGoals, setPastGoals] = useState([]);

  useEffect(async () => {
    await getUserGoals();
    console.log(userGoals);
    //eslint-disable-next-line
  }, []);

  //determine if there are active and/or past goals

  // userGoals.map(goal => {
  //   let time = moment.utc().startOf('day').diff(startDate, 'days') + 1;
  //   if (time < 0)
  //     time = 0;
  //   else if (time > duration)
  //     return null;
  // })

  //current goals
  let currentItems = null;

  if (loading)
    currentItems = (
      <li className='collection-item'>
        <h6>Loading...</h6>
      </li>
    )
  else if (userGoals !== null && userGoals.length === 0 && !loading) {
    currentItems = (
      <li className='collection-item center'>
        You have no current goals. Add one!
      </li>
    );
  }
  else {
    currentItems = (
      <React.Fragment>
        {userGoals && userGoals.map(goal => <GoalItem goal={goal} key={goal._id} />)}
      </React.Fragment>
    );
  }

  //past goals
  let pastItems = null;

  if (loading)
    pastItems = (
      <li className='collection-item'>
        <h6>Loading...</h6>
      </li>
    )
  else if (userGoals !== null && userGoals.length === 0 && !loading) {
    pastItems = (
      <li className='collection-item center'>
        You have no past goals. Add one!
      </li>
    );
  }
  else {
    pastItems = (
      <React.Fragment>
        {userGoals && userGoals.map(goal => <GoalItem goal={goal} key={goal._id} />)}
      </React.Fragment>
    );
  }

  return (
    <React.Fragment>
      <ul className='collection'>
        <li className='collection-header'>
          <h2>Current Goals</h2>
        </li>
        {currentItems}
        <li className='collection-footer'>
          <Link to='/goalform' className='text-secondary'>
            <p><i className='fas fa-plus'/> Add Goal</p>
          </Link>
        </li>
      </ul>
      <ul className='collection'>
        <li className='collection-header'>
          <h2>Past Goals</h2>
        </li>
      </ul>
    </React.Fragment>
  );
};

export default Goals;
