import React, { useContext, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import userimg from '../../resources/userimg.png';
import PropTypes from 'prop-types';
import RequestContext from '../../contexts/requests/requestContext';
import FriendContext from '../../contexts/friends/friendContext';
import AlertContext from '../../contexts/alerts/alertContext';

const SearchItem = ({history, status, user: {firstName, lastName, email, _id}}) => {

  const requestContext = useContext(RequestContext);
  const { addRequest, deleteRequest, requestsError, clearRequestsError } = requestContext;

  const friendContext = useContext(FriendContext);
  const { addFriend, setCurrentFriend } = friendContext;

  const alertContext = useContext(AlertContext);
  const { setAlert } = alertContext;

  //configure button(s) based on status
  let buttonMsg = 'Add';
  let buttonClass = 'btn-primary';

  if (status === 'friend') {
    buttonMsg = 'Friend!';
    buttonClass = 'btn-blue';
  } else if (status === 'received') {
    buttonMsg = 'Accept';
    buttonClass = 'btn-success';
  } else if (status === 'sent') {
    buttonMsg = 'Delete';
    buttonClass = 'btn-danger';
  }

  useEffect(() => {
    if(requestsError) {
      setAlert(requestsError);
      clearRequestsError();
    }
    //eslint-disable-next-line
  }, [requestsError]);

  const handleClick = e => {
    if (status === 'stranger') {
      addRequest(_id);
    } else if (status === 'friend') {
      console.log('friend button works 1')
      setCurrentFriend(_id);
      console.log('friend button works 2')
      //history destructured from props to allow other destructuring
      history.push(`/friend/${_id}`);
    } else if (status === 'received' && e.target.name === 'btn1') {
      addFriend(_id);
      deleteRequest(_id);
    } else if (status === 'received' && e.target.name === 'btn2') {
      deleteRequest(_id);
    } else if (status === 'sent') {
      deleteRequest(_id);
    }
  }

  const avatar = null;

  return (
    <li className='search-item'>
      {status === 'received' &&
        <p className='center btn-caution border-bottom'>{firstName} sent you a friend request!</p>
      }
      {status === 'sent' &&
        <p className='center btn-caution border-bottom'>You sent {firstName} a friend request!</p>
      }
      <div className='search-link'>
        <div className="flex">
          <img
            src={avatar !== null ? avatar : userimg}
            alt={firstName.charAt(0)}
          />
        </div>
        <div className='search-container'>
          <span className='search-name'>{firstName} {lastName}</span>
          <br/>
          <span className='search-email'>{email}</span>
        </div>
        <div className='right'>
          <button
            className={`search-btn ${buttonClass} block`}
            onClick={handleClick}
            name='btn1'
          >
            {buttonMsg}
          </button>
          {status === 'received' &&
            <button
              className='search-btn btn-danger block'
              onClick={handleClick}
              name='btn2'
            >
              Reject
            </button>
          }
        </div>
      </div>
    </li>
  );
};

SearchItem.propTypes = {
  user: PropTypes.object.isRequired,
  status: PropTypes.string.isRequired,
};

export default SearchItem;
