import React, { useContext, useEffect } from 'react';
import FriendContext from '../../../contexts/friends/friendContext';
import FriendGoals from './FriendGoals';
import FriendFriends from './FriendFriends';

const FriendPage = props => {
  const friendContext = useContext(FriendContext);
  const { friendCurrent, friendIds, clearCurrentFriend, getCurrentFriendFriends, getCurrentFriendGoals } = friendContext;

  useEffect(() => {
    //redirect if users are not friends
    console.log('check')
    if(!friendIds.includes(props.match.params.id))
      props.history.goBack();

    console.log('check2')

    //get friend goals and friends
    getCurrentFriendGoals(props.match.params.id);
    getCurrentFriendFriends(props.match.params.id);

    console.log('check3')
    //clear current friend on unmount
    return () => {
      console.log('clear')
      clearCurrentFriend();
    }
    //eslint-disable-next-line
  }, [friendCurrent]);

  return (
    <div className='grid-2'>
      <FriendGoals/>
      <FriendFriends/>
    </div>
  )
};

export default FriendPage
