import React, { useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import FriendContext from '../../contexts/friends/friendContext';
import FriendItem from './FriendItem';

const Friends = () => {
  const friendContext = useContext(FriendContext);
  const { getFriends, friends, loading } = friendContext;

  useEffect(() => {
    getFriends();
    //eslint-disable-next-line
  }, []);

  //populate list
  let listItems = '';
  if (loading) {
    listItems = (
      <li className='collection-item center collection-item-block'>
        Loading...
      </li>
    );
  }
  else if (loading.length === 0) {
    const message = 'You have no friends :('
    listItems = (
      <li className='collection-item center collection-item-block'>
        {message}
        <br/>
        It's okay, I'm right there with you.
      </li>
    );
  }
  else {
    listItems = (
      <React.Fragment>
        {friends.map(friend => <FriendItem friend={friend} key={friend._id} />)}
      </React.Fragment>
    )
  }


  return (
    <ul className='collection'>
      <li className='collection-header'>
        <h2>Friends!</h2>
      </li>
      {listItems}
      <li className='collection-footer'>
        <Link to='/search' className='text-sedondary'>
          <p><i className='fas fa-plus'/> Add Friend</p>
        </Link>
      </li>
    </ul>
  );
};

export default Friends;
