import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import CompFITition from '../resources/CompFITition.png';
import AuthContext from '../../contexts/auth/authContext';

const Navbar = () => {
  const authContext = useContext(AuthContext);
  const { isAuthenticated, user } = authContext;

  const authLinks = (
    <React.Fragment>
      <li>
        <Link to='/'>Hello, {user && user.firstName}!</Link>
      </li>
      <li>
        <Link to='/'>Log out</Link>
      </li>
    </React.Fragment>
  );

  const guestLinks = (
    <React.Fragment>
      <li>
        <Link to='/register'>Register</Link>
      </li>
      <li>
        <Link to='/login'>Log In</Link>
      </li>
    </React.Fragment>
  );

  return (
    <nav className='navbar bg-primary'>
      <div>
        <h1 className='text-secondary'>
          <i className='fas fa-trophy'/> CompFITition
          <br/>
          <i className='fas fa-running'/> CompFITition
          <br/>
          <i className='fas fa-medal'/> CompFITition
        </h1>

        <ul>
          {isAuthenticated ? authLinks : guestLinks}
        </ul>
      </div>
    </nav>
  )
};

export default Navbar;
