import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import GoalContext from '../../contexts/goals/goalContext';
import GoalItem from './GoalItem';

const Goals = () => {
  const goalContext = useContext(GoalContext);
  const { getUserGoals, userGoals, loading } = goalContext;

  const [past, setPast] = useState(false);
  const [active, setActive] = useState(false);

  const setStatus = (past, active) => {
    if (past)
      setPast(true);
    if (active)
      setActive(true);
  };

  useEffect(() => {
    getUserGoals();
    //eslint-disable-next-line
  }, []);

  //current items
  let currentItems = null;

  if (loading)
    currentItems = (
      <li className='collection-item'>
        <h6>Loading...</h6>
      </li>
    )
  else {
    currentItems = (
      <React.Fragment>
        {userGoals && userGoals.map(goal => <GoalItem goal={goal} key={goal._id} setStatus={() => setStatus(past, active)} />)}
      </React.Fragment>
    );
  }
  if (!active) {
    currentItems = (
      <li className='collection-item center'>
        You have no current goals. Add one!
      </li>
    );
  }

  //past items
  let pastItems = null;

  if (!past) {
    pastItems = (
      <React.Fragment>
        {userGoals && userGoals.map(goal => <GoalItem goal={goal} key={goal._id} />)}
      </React.Fragment>
    );
  }

  return (
    <React.Fragment>
      <ul className='collection'>
        <li className='collection-header'>
          <h2>Current Goals</h2>
        </li>
        {currentItems}
        <li className='collection-footer'>
          <Link to='/goalform' className='text-secondary'>
            <p><i className='fas fa-plus'/> Add Goal</p>
          </Link>
        </li>
      </ul>
      {!past && 
        <ul className='collection'>
          <li className='collection-header'>
            <h2>Past Goals</h2>
          </li>
          {pastItems}
        </ul>
      }
    </React.Fragment>
  );
};

export default Goals;
