import React, { useContext, useState, useEffect } from 'react';
import AuthContext from '../../contexts/auth/authContext';
import AlertContext from '../../contexts/alerts/alertContext';

const UserPage = props => {
  const authContext = useContext(AuthContext);
  const { user, updateUser, isAuthenticated, error, clearUserErrors } = authContext;

  const alertContext = useContext(AlertContext);
  const { setAlert, clearAlerts } = alertContext;

  const [current, setCurrent] = useState({
    firstName: '',
    lastName: '',
    alias: '',
    searchable: true,
    email: '',
    password: '',
    newPassword: '',
    newPassword2: '',
    resetPassword: false,
    editUser: false
  });

  //populate current with user values
  useEffect(() => {
    setCurrent({...user});
  },[user]);

  //redirect if not authenticated, set alert if error
  useEffect(() => {
    if (!isAuthenticated || user === null)
      props.history.push('/');
    if (error) {
      setAlert(error);
      clearUserErrors();
    }
    //eslint-disable-next-line
  }, [error, isAuthenticated, props.history]);


  const { firstName, lastName, alias, searchable, email, password, newPassword, newPassword2, resetPassword, editUser} = current;

  const handleDelete = () => {

  };

  const handleEdit = () => {
    setCurrent({ ...current, editUser: true });
  };

  const handleSave = () => {

  }

  const handleChange = e => {
    setCurrent({...current, [e.target.name]: e.target.value});
  }

  const handleClick = () => {
    setCurrent({ ...current, searchable: !searchable });
  };

  return (
    <div className='form-container'>
      <h1>User Profile</h1>
      <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Et beatae inventore porro non aut incidunt excepturi expedita molestias quod ex unde veniam omnis, fugit corporis saepe placeat sit veritatis itaque?</p>
      {editUser && (
      <form onSubmit={handleSubmit}>
        {/*First Name*/}
        <div className="form-group">
          <label htmlFor='firstName'>First Name</label>
          <input
            type='text'
            value={firstName}
            name='firstName'
            onChange={handleChange}
          />
        </div>
        {/*Last Name*/}
        <div className="form-group">
          <label htmlFor='lastName'>Last Name</label>
          <input
            type='text'
            value={lastName}
            name='lastName'
            onChange={handleChange}
          />
        </div>
        {/*Alias*/}
        <div className="form-group">
          <label htmlFor='alias'>Alias (optional - the name your friends will see)</label>
          <input
            type='text'
            value={alias}
            name='alias'
            onChange={handleChange}
          />
        </div>
        {/*Email Address*/}
        <div className="form-group">
          <label htmlFor='email'>Email Address</label>
          <input
            type='email'
            value={email}
            name='email'
            onChange={handleChange}
          />
        </div>
        {/*Password*/}
        <div className="form-group">
          <label htmlFor='password'>Password</label>
          <input
            type='password'
            value={password}
            name='password'
            onChange={handleChange}
          />
        </div>
        {/*Confirm Password*/}
        <div className="form-group">
          <label htmlFor='password2'>Confirm Password</label>
          <input
            type='password'
            value={password2}
            name='password2'
            onChange={handleChange}
          />
        </div>
        {/*Privacy*/}
        <div className="form-group">
          <label className='block'>Privacy</label>
          <label className='switch'>
            <input
              type='checkbox'
              checked={searchable}
              name='searchable'
              onChange={handleClick}
            />
            <span className='slider round'/>
          </label>
          <span className='register-span'>Are other users allowed to search for your name, email, and alias? This is how friends will find you.</span>
        </div>
        {/*Save Button*/}
        <input 
          type='submit'
          value='Save User'
          className='btn btn-primary btn-block'
        />
      </form>
      )}
      {/*Edit and Delete Button*/}
      {!editUser && (
        <React.Fragment>
          <input
            type='button'
            value='Edit User'
            className='btn btn-primary'
            onClick={handleEdit}
          />
          <input
            type='button'
            value='Delete User'
            className='btn btn-danger'
            onClick={handleDelete}
          />
        </React.Fragment>
      )}
    </div>
  )
}

export default UserPage;
