import React, { useContext, useState } from 'react';
import AuthContext from '../../contexts/auth/authContext';
import AlertContext from '../../contexts/alerts/alertContext';

const UserPage = props => {
  const authContext = useContext(AuthContext);
  const { user, updateUser, isAuthenticated, error, clearUserErrors } = authContext;

  const alertContext = useContext(AlertContext);
  const { setAlert, clearAlerts } = alertContext;

  //redirect if not authenticated, set alert if error
  useEffect(() => {
    if (!isAuthenticated || user === null)
      props.history.push('/');

    if (error) {
      setAlert(error);
      clearUserErrors();
    }
    //eslint-disable-next-line
  }, [error, isAuthenticated, props.history]);

  const [current, setCurrent] = useState({
    firstName: '',
    lastName: '',
    alias: '',
    searchable: true,
    email: '',
    password: '',
    newPassword: '',
    newPassword2: '',
    resetPassword: false
  });

  setCurrent({...user});

  const { firstName, lastName, alias, searchable, email, password, newPassword, newPassword2, resetPassword} = current;

  const handleSubmit = e => {
    e.preventDefault();
  }

  const handleChange = e => {
    setCurrent({...current, [e.target.name]: e.target.value});
  }

  const handleClick = e => {
    setUser({ ...user, searchable: !searchable });
  };

  return (
    <div>
      {props.match.params.id}

      <input
        type='button'
        value='Edit User'
        className='btn btn-primary'
      />
      <input
        type='button'
        value='Delete User'
        className='btn btn-danger'
      />

    </div>
  )
}

export default UserPage;
